create table Curve (
id                  INT generated by default as identity not null,
startX              double                         not null,
startY              double                         not null,
midX                double                         not null,
midY                double                         not null,
endX                double                         not null,
endY                double                         not null,
centreY             double                         not null,
centreX             double                         not null,
alpha               double                         not null,
curvatureRatio      double                         not null,
arcLength           double                         not null,

constraint PK_Curves primary key(id)
);

create table Results (
id                   INT generated by default as identity not null,
curveId                 INT                            not null,
curvatureRatio          double                         not null,
arcLength               double                         not null,
discretizationParts     INT                            not null,
linearLength            double                         not null,
bezierLength            double                         not null,

constraint PK_Results primary key(id),
constraint FK_Results_Curve foreign key (curveId)
      references Curve (id)
);

